import React from 'react';
import Axios from 'axios';
import './styles.css';

class ContactForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      status: false,
    };
  }

  handleInputChange = (value, key) => {
    this.setState({
      [key]: value,
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { name, email } = this.state;
    let payload = {
      name: name,
      email: email,
    };

    let response = await Axios.post('http://localhost:3001/insertUserDetails', JSON.stringify({ payload }));

    if (response.data && response.data.status === true) {
      this.setState({
        status: true,
        name: response.data.name,
        email: response.data.email,
      });
    }
  };

  render() {
    const { name, email } = this.state;

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label for="nameImput">Name</label>
            <input
              type="text"
              name="name"
              value={this.state.name}
              onChange={(e) => {
                this.handleInputChange(e.target.value, 'name');
              }}
            />
          </div>
          <div className="form-group">
            <label for="emailImput">Email</label>
            <input
              name="email"
              type="email"
              value={this.state.email}
              onChange={(e) => {
                this.handleInputChange(e.target.value, 'email');
              }}
            />
          </div>
          <button onClick={this.handleSubmit}>Submit</button>
        </form>
      </div>
    );
  }
}

class MainTitle extends React.Component {
  render() {
    return <h1>React Form example</h1>;
  }
}

class App extends React.Component {
  render() {
    return (
      <div>
        <MainTitle />
        <ContactForm />
      </div>
    );
  }
}

export default App;
